import React from 'react';
import ReactDOM from 'react-dom';

class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = { lat: "Searching", lon: "Searching", address: "Searching", appCode: "7RPWqLqKW0C5u3XQSKrohw", appId: "wkVafqaAkhcWml7qHCyo"};

        window.navigator.geolocation.getCurrentPosition(
            (position) => {
                console.log(position);
                this.setState({ lat: position.coords.latitude, lon: position.coords.longitude });
                fetch("https://reverse.geocoder.api.here.com/6.2/reversegeocode.json?app_id="+this.state.appId+
                "&app_code="+this.state.appCode+
                "&mode=retrieveAddresses&prox="+
                this.state.lat+","+this.state.lon+",250")
                    .then(function(response) {
                        return response.json();
                    })
                    .then(function(myJson) {
                        let address = myJson.Response.View[0].Result[0].Location.Address.Label;
                        this.setState({ address: address})
                    });
            },
            (err) => {
                this.setState({ lat: err.message, lon: "-", address:"-"});
            }
        );
    }

    render() {
        return (
            <div>Latitude: {this.state.lat} Longitude: {this.state.lon} Address: {this.state.address}</div>
        );
    }
}
ReactDOM.render(<App/>, document.querySelector('#root'));
