import React from 'react';
import ReactDOM from 'react-dom';

class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = { 
            lat: null, 
            long: null, 
            address: null, 
            appCode: "7RPWqLqKW0C5u3XQSKrohw", 
            appId: "wkVafqaAkhcWml7qHCyo", 
            errorMessage: null};

        window.navigator.geolocation.getCurrentPosition(
            (position) => {
                console.log(position);
                this.setState({ lat: position.coords.latitude, long: position.coords.longitude });
                fetch(`https://reverse.geocoder.api.here.com/6.2/reversegeocode.json?app_id=${this.state.appId}&app_code=${this.state.appCode}&mode=retrieveAddresses&prox=${this.state.lat},${this.state.long}`)
                    .then((response) => {
                        return response.json();
                    })
                    .then((myJson) => {
                        let address = myJson.Response.View[0].Result[0].Location.Address;
                        console.log(address);
                        this.setState({ address: `${address.City}, ${address.State}, ${address.Country}`});
                    });                
            },
            (err) => {
                this.setState({errorMessage: err.message});
            }
        );
    }

    render() {
        if (this.state.errorMessage && !this.state.lat){
            return (
                <div className="ui container">
                    <div className="ui negative message">
                        <div className="header">                        
                            {this.state.errorMessage}
                        </div>
                    </div>
                </div>
            );
        }
        if(!this.state.errorMessage && !this.state.lat){
            return (
                <div className="ui container">

                    <div className="ui blue message">
                        <div className="header">                        
                            Loading...
                        </div>
                    </div>
                </div>
            );
        }

        return (
            <div className="ui container">
                <pre style={{
                    borderTop: '2px solid black',
                    borderBottom: '2px solid black',
                    padding: '10px',
                    display: 'block',
                    }}>
                    <strong>Latitude:</strong>  {this.state.lat}<br/>
                    <strong>Longitude:</strong> {this.state.long}<br/>
                    <strong>Address:</strong>   {this.state.address}
                </pre>
            </div>
            );
    }
}
ReactDOM.render(<App/>, document.querySelector('#root'));
