import React from 'react';
import ReactDOM from 'react-dom';

class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = { lat: 0, lon: 0, key: "Amr3PGzJIUIluG7heHjv-nfdoL7gNF8eEaqvajrfTWxSJxnajfmjU44BmXPcX7Ot"};

        window.navigator.geolocation.getCurrentPosition(
            (position) => {
                console.log(position);
                this.setState({ lat: position.coords.latitude, lon: position.coords.longitude });
                fetch("http://dev.virtualearth.net/REST/v1/locationrecog/"+this.state.lat+","+this.state.lon+"?key="+this.state.key+"&includeEntityTypes=address&output=json")
                    .then(function(myJson) {
                        console.log(myJson);
                    });
            },
            (err) => {
                this.setState({ lat: err.message });
            }
        );
    }

    render() {
        return (
            <div>Latitude: {this.state.lat}</div>
        );
    }
}
ReactDOM.render(<App/>, document.querySelector('#root'));
